import { useState, useEffect } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { MapPin, Navigation, Shield } from 'lucide-react';

interface LocationPermissionModalProps {
  onPermissionGranted: (position: GeolocationPosition) => void;
  onPermissionDenied: () => void;
}

export function LocationPermissionModal({ onPermissionGranted, onPermissionDenied }: LocationPermissionModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [permissionStatus, setPermissionStatus] = useState<'unknown' | 'granted' | 'denied'>('unknown');

  useEffect(() => {
    checkPermissionStatus();
  }, []);

  const checkPermissionStatus = async () => {
    if ('permissions' in navigator) {
      try {
        const permission = await navigator.permissions.query({ name: 'geolocation' as PermissionName });
        if (permission.state === 'granted') {
          setPermissionStatus('granted');
          getCurrentLocation();
        } else if (permission.state === 'denied') {
          setPermissionStatus('denied');
          setIsOpen(true);
        } else {
          setPermissionStatus('unknown');
          setIsOpen(true);
        }
      } catch (error) {
        console.error('Error checking permission:', error);
        setIsOpen(true);
      }
    } else {
      setIsOpen(true);
    }
  };

  const getCurrentLocation = () => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setPermissionStatus('granted');
          onPermissionGranted(position);
          setIsOpen(false);
        },
        (error) => {
          console.error('Error getting location:', error);
          setPermissionStatus('denied');
          onPermissionDenied();
          setIsOpen(false);
        },
        {
          enableHighAccuracy: true,
          timeout: 10000,
          maximumAge: 60000
        }
      );
    }
  };

  const requestLocationPermission = () => {
